//鼠标监听导航动画
$(function(){
　　var nav=$("#nav"),
　　　　 win=$(window),
　　　　 doc=$(document);　
       if(doc.scrollTop()>=60){　　
         $('#nav').addClass('navbar-fixed-top');
       }else{
         $('#nav').removeClass('navbar-fixed-top');
       }　　
　　　　win.scroll(function(){
　　　　　if(doc.scrollTop()>=60){
           $("#nav").addClass('navbar-fixed-top');
          }else{
            $("#nav").removeClass('navbar-fixed-top');
          }
        })
})
